<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');/* * Developer : pan-x (info@pan-x.com) * All code (c)2013 pan-x all rights reserved */ class Home extends CI_Controller {    function Home(){        parent::__construct();        // load model        $this->load->model('userModel','',TRUE);    }    public function index() {        // debug maw        $this->client('maw');        $this->load->library('MyMenu');        $menu = new MyMenu;        $this->load->library('MyFooter');        $footer = new MyFooter;        $data = array();        $data['navigation'] = $menu->show_menu();                if($_SESSION['user']['loginLevel'] == 1) {            if(isset($_SESSION['household']['household_id'])) {                                $data['mainContent'] = $this->load->view('home_household', $data, true);            } else {                $data['mainContent'] = $this->load->view('home_personaldata', $data, true);                            }        } else {            $data['mainContent'] = $this->load->view('home', $data, true);        }                        $data['homeTitle'] = 'Home';		$data['headerTitle']  =  $_SESSION ['client']['client_name'];        $data['footer'] = $footer->show_footer();        $this->load->view('main_template', $data);    }// set client session    public function client($client_url) {        $this->load->model('clientModel','',TRUE);        $client = $this->clientModel->load_session_by_url($client_url);        // debug        //redirect('home','refresh');    }}/* End of file home.php *//* Location: ./application/controllers/home.php */