<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');/* * Developer : pan-x (info@pan-x.com) * All code (c)2013 pan-x all rights reserved */class Home extends CI_Controller {    function Home(){        parent::__construct();        // load model        $this->load->model('userModel','',TRUE);    }    public function index() {        $this->load->library('MyMenu');        $menu = new MyMenu;        $this->load->library('MyFooter');        $footer = new MyFooter;        $data = array();        $data['navigation'] = $menu->show_menu();        $data['homeTitle'] = 'Home';        $data['footer'] = $footer->show_footer();        $this->load->view('main_template', $data);    }}/* End of file home.php *//* Location: ./application/controllers/home.php */